import React from 'react'
import {connect} from "react-redux"
import buyIceCream from "../redux/iceCream/iceCreamActions"
import store from "../redux/store"
console.log(store.getState())

const IceCreamContainer = (props) => {
let states=store.getState().ıceCream;
  console.log(states)
  return (
    <div>
  
        <h2>Number of Cakes:{props.numofIceCream}</h2>
        <button onClick={()=>props.buyIceCream()}>Buy IceCream</button>
    </div>
  )
}

const mapStateToProps=state=>{//1
    return{
      numofIceCream:state.numofIceCream
    }
}
const mapDispatchToProps=dispatch=>{//2
  return{
    buyIceCream:()=>dispatch(buyIceCream())
  }
}

export default connect(mapStateToProps,mapDispatchToProps)(IceCreamContainer)//3

