import React,{useState} from 'react'
import {connect} from "react-redux"
import { buyCake, refundCake } from '../redux/cake/cakeActions'
import { buyIceCream } from '../redux/iceCream/iceCreamActions'
const ItemContainer = (props) => {
    const[editNumber,setEditNumber]=useState(1);  
    console.log(editNumber)
    console.log(props)
    return (
    <div>
      <h2>Change Cake Pıece</h2>
<input type="number" placeholder='EditCakeNumber' value={editNumber} onChange={(e)=>setEditNumber(e.target.Value)} />
<button onClick={()=>props.editItem(editNumber)}>Change</button>
       <h3>Item:{props.item}</h3> 
       <button onClick={props.buyItem}>Buy </button>
    </div>
  )
}
const mapStateToProps=(state,ownProps)=>{//if cake props send then  ownProps.cake:true,conditional: state.cake.numofCakes else state.ıceCream.numofIceCream
const itemState=ownProps.cake ? state.cake.numofCakes :state.ıceCream.numofIceCream
console.log(state)
return{

    item:itemState

}
}

const mapDispatchToProps=(dispatch,ownProps)=>{
    const dispatchFunction=ownProps.cake ?()=>dispatch(buyCake()):()=>dispatch(buyIceCream())
    return{
        buyItem:dispatchFunction,
        editItem:(editNum)=>dispatch(refundCake(editNum))
    }
}
export default  connect(mapStateToProps,mapDispatchToProps)(ItemContainer)
