import{BUY_CAKE} from "./cakeTypes"
import { REFUND_CAKE } from "./cakeTypes"
const initialState={
    numofCakes:15
  }

  const cakeReducer=(state=initialState,action)=>{
    switch(action.type){
        case BUY_CAKE:
            return{...state,numofCakes:state.numofCakes-1}//initial state  içindeki numofcakes değerinin 1 eksiğini içeren obje döndür. önce statei kopyala sonra içerğini değiştir.
        case REFUND_CAKE:
            return{
              ...state,
              numofCakes
            }
            default: return state
        }
  }
  
export default cakeReducer;
