import { BUY_CAKE } from "./cakeTypes";
import { REFUND_CAKE } from "./cakeTypes";
export const buyCake=()=>{
    return{
        type:BUY_CAKE
    }
}

export const custombuyCake=(customNumber)=>{
    return{
        type:CUSTOM_BUY_CAKE,
        payload:customNumber,

    }
}

export const refundCake=()=>{
    return{
        type:REFUND_CAKE
    }
}
