import React, { useState } from "react";
import "./style.css";
import { connect } from "react-redux";
import { listeyeEkle,isaretle,TumunuTemizle,Guncelleme} from "./reducers/actions";
import { listedenSıl } from "./reducers/actions";
function App(props) {
  console.log(props.liste)
const[ınputValue,setInputValue]=useState("");
const[editMode,setEditMode]=useState(false)
const[editItemId,setEditItemId]=useState(0);
const handleUpdate = (id, baslik) => {
  // find the list item with the given title
  const selectedItem = props.liste.filter(list => list.baslik === baslik)[0];
  console.log(selectedItem)
  // update the state with the id of the selected item
  setInputValue("")
  setEditItemId(selectedItem.id);
  setEditMode(true);
  setInputValue(selectedItem.baslik);

};

console.log(ınputValue)

// ...





  return (
    <div className="App">
      <h1>Yapılacaklar Listesi</h1>
      <div className="ekleme_formu">
        <input placeholer="listeye ekle" value={ınputValue} onChange={(e)=>setInputValue(e.target.value)} />
    {editMode ? <button onClick={()=> props.Guncelleme(editItemId, ınputValue)}>Editt</button> :   <button onClick={()=>{ setInputValue(""); props.listeyeEkle(ınputValue)}}>Ekle</button>
}

      </div>


      <div className="liste">
       { props.liste.map((list)=>{
        return<div  onClick={()=>props.isaretle(list.id)} key={list.id} className={list.tamamlandi ? "yapildi":""}>
        <p>{list.baslik} <span onClick={()=>handleUpdate(list.id,list.baslik)}>Güncelle</span>  <span><button onClick={()=>props.listedenSıl(list.id)}>Sil</button></span></p>
        </div>
       })}
      </div>

      <button onClick={()=>props.TumunuTemizle()}  className="temizle">Tamamlananları Temizle</button>
    </div>
  );
}
const mapStateProps=state=>{
  return{
    liste:state.liste
  }
}

export default connect(mapStateProps, { listeyeEkle, listedenSıl,isaretle,TumunuTemizle,Guncelleme})(App);//4.adım
