//2.adım
import { EKLE, ISARETLE } from "./actions";
import { SIL } from "./actions";
import { TEMIZLE } from "./actions";
const INITIAL_STATE = {
    liste:[
      { id: 1, baslik: "Alisveris Yap", tamamlandi: false },
    { id: 2, baslik: "Fatura ode", tamamlandi: true },
    { id: 3, baslik: "Kimyacıyı bul", tamamlandi: true }
    ]
}
  

export const reducer=(state=INITIAL_STATE,action)=>{//5.adım
    switch(action.type){
      case EKLE:
        return{
           ...state,
            liste:[...state.liste,{
            id:state.length+1,
            baslik:action.payload,
            tamamlandi:false
           }]
        }

        case SIL:
          return {
            ...state,
            liste: state.liste.filter((list) => list.id !== action.payload),
          };

          case ISARETLE:
            return {
              ...state,
              liste:state.liste.map((item)=>item.id===action.payload ?{...item,tamamlandi:!item.tamamlandi}:item)
            };
        

            case TEMIZLE:
              return {
                ...state,
                liste:state.liste.filter((item)=>item.id)
              };
        default:return state;
        
      }  
  

}

