import React, { useState } from "react";
import "./style.css";
import { connect } from "react-redux";
import { listeyeEkle, isaretle, TumunuTemizle, Guncelleme, listedenSıl } from "./reducers/actions";

function App(props) {
  const [inputValue, setInputValue] = useState("");
  const [editId, setEditId] = useState(null); // eklendi
  const [editInput, setEditInput] = useState("");

  const handleUpdate = (id, baslik) => {
    setEditId(id);
    setInputValue(baslik);
  };

  const handleSave = () => {
    if (editId) {
      props.Guncelleme(editId, editInput);
      setEditId(null);
    } else {
      props.listeyeEkle(inputValue);
    }
    setInputValue("");
    setEditInput("");
  };

  return (
    <div className="App">
      <h1>Yapılacaklar Listesi</h1>
      <div className="ekleme_formu">
        <input
          placeholder="listeye ekle"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
        />
        <button onClick={handleSave}>{editId ? "Kaydet" : "Ekle"}</button>
      </div>

      <div className="liste">
        {props.liste.map((list) => {
          return (
            <div
              onClick={() => props.isaretle(list.id)}
              key={list.id}
              className={list.tamamlandi ? "yapildi" : ""}
            >
              <p>
                {editId === list.id ? (
                  <input
                    value={editInput}
                    onChange={(e) => setEditInput(e.target.value)}
                  />
                ) : (
                  <span onClick={() => handleUpdate(list.id, list.baslik)}>
                    {list.baslik}
                  </span>
                )}
                <span>
                  <button onClick={() => props.listedenSıl(list.id)}>Sil</button>
                </span>
              </p>
            </div>
          );
        })}
      </div>

      <button onClick={() => props.TumunuTemizle()} className="temizle">
        Tamamlananları Temizle
      </button>
    </div>
  );
}

const mapStateProps = (state) => {
  return {
    liste: state.liste,
  };
};

export default connect(mapStateProps, {
  listeyeEkle,
  listedenSıl,
  isaretle,
  TumunuTemizle,
  Guncelleme,
})(App);
