import React, { useState } from "react";
import "./style.css";
import { connect } from "react-redux";
import { listeyeEkle } from "./reducers/actions";
import { listedenSıl } from "./reducers/actions";

class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false };
  }

  static getDerivedStateFromError(error) {
    // Update state so the next render will show the fallback UI.
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    // You can also log the error to an error reporting service
    console.log("Error:", error);
    console.log("Error Info:", errorInfo);
  }

  render() {
    if (this.state.hasError) {
      // You can render any custom fallback UI
      return <h2>Something went wrong.</h2>;
    }

    return this.props.children;
  }
}

function App(props) {
  console.log(props.liste);
  const [ınputValue, setInputValue] = useState("");

  return (
    <div className="App">
      <h1>Yapılacaklar Listesi</h1>
      <div className="ekleme_formu">
        <input
          placeholer="listeye ekle"
          value={ınputValue}
          onChange={(e) => setInputValue(e.target.value)}
        />
        <button onClick={() => props.listeyeEkle(ınputValue)}>Ekle</button>
      </div>

      <div className="liste">
        <ErrorBoundary>
          {props.liste.map((list) => {
            return (
              <div key={list.id} className={list.tamamlandi ? "yapildi" : ""}>
                <p>
                  {list.baslik}{" "}
                  <button onClick={() => props.listedenSıl(list.id)}>Sil</button>
                </p>
              </div>
            );
          })}
        </ErrorBoundary>
      </div>

      <button className="temizle">Tamamlananları Temizle</button>
    </div>
  );
}

const mapStateProps = (state) => {
  return {
    liste: state.liste,
  };
};

export default connect(mapStateProps, { listeyeEkle, listedenSıl })(App); //4.adım app componentim bu
