import React, { useState } from "react";
import "./style.css";
import { connect } from "react-redux";
import { listeyeEkle,isaretle } from "./reducers/actions";
import { listedenSıl } from "./reducers/actions";
function App(props) {
  console.log(props.liste)
const[ınputValue,setInputValue]=useState("");

  return (
    <div className="App">
      <h1>Yapılacaklar Listesi</h1>
      <div className="ekleme_formu">
        <input placeholer="listeye ekle" value={ınputValue} onChange={(e)=>setInputValue(e.target.value)} />
        <button onClick={()=>{ setInputValue(""); props.listeyeEkle(ınputValue)}}>Ekle</button>
      </div>

      <div className="liste">
       { props.liste.map((list)=>{
        return<div  onClick={()=>props.isaretle(item.id)} key={list.id} className={list.tamamlandi ? "yapildi":""}>
        <p>{list.baslik} <button onClick={()=>props.listedenSıl(list.id)}>Sil</button></p>
        </div>
       })}
      </div>

      <button onClick=""  className="temizle">Tamamlananları Temizle</button>
    </div>
  );
}
const mapStateProps=state=>{
  return{
    liste:state.liste
  }
}

export default connect(mapStateProps, { listeyeEkle, listedenSıl,isaretle})(App);//4.adım
