export const TEMIZLE="TEMIZLE"
export const ISARETLE="ISARETLE"

//actions
export const listeyeEkle=(baslik)=>{
return{
    type:EKLE,payload:baslik
}
}

export const listedenSıl=(id)=>{
    return{
        type:SIL,payload:id
    }
    }

export const isaretle=(id)=>{
    return{
        type:ISARETLE,payload:id
    }
}

