import React, { useState } from "react";
import "./style.css";
import { connect } from "react-redux";
const INITIAL_STATE = [
  { id: 1, baslik: "Alisveris Yap", tamamlandi: false },
  { id: 2, baslik: "Fatura ode", tamamlandi: true }
];

function App(props) {
  const [liste, setListe] = useState(INITIAL_STATE);

  return (
    <div className="App">
      <h1>Yapılacaklar Listesi</h1>
      <div className="ekleme_formu">
        <input placeholer="listeye ekle" />
        <button>Ekle</button>
      </div>
      <div className="liste">
        {liste.map(item => (
          <div className={item.tamamlandi ? "yapildi" : ""}>{item.baslik}</div>
        ))}
      </div>
      <button className="temizle">Tamamlananları Temizle</button>
    </div>
  );
}
const mapStateProps=state=>{
  return{
    liste:state.liste
  }
}

export default connect(mapStateProps(App));//4.adım
