import React, { useState } from "react";
import "./style.css";
import { connect } from "react-redux";
import { listeyeEkle,isaretle,TumunuTemizle,Guncelleme} from "./reducers/actions";
import { listedenSıl } from "./reducers/actions";
function App(props) {
  console.log(props.liste)
const[ınputValue,setInputValue]=useState("");
const[editInput,setEditInput]=useState("");

const handleUpdate=(id,baslik)=>{
setInputValue(baslik)
  props.Guncelleme(id,editInput)
}
  return (
    <div className="App">
      <h1>Yapılacaklar Listesi</h1>
      <div className="ekleme_formu">
      </div>

    <input type="text" name="" id="" value={editInput} onChange={(e)=>setEditInput(e.target.value)} />
    <button onClick={()=>setEditInput()}>Edit</button>
      <div className="liste">
       { props.liste.map((list)=>{
        return<div  onClick={()=>props.isaretle(list.id)} key={list.id} className={list.tamamlandi ? "yapildi":""}>
        <p>{list.baslik}  <span><button onClick={()=>props.listedenSıl(list.id)}>Sil</button></span></p>
        </div>
       })}
      </div>

      <button onClick={()=>props.TumunuTemizle()}  className="temizle">Tamamlananları Temizle</button>
    </div>
  );
}
const mapStateProps=state=>{
  return{
    liste:state.liste
  }
}

export default connect(mapStateProps, { listeyeEkle, listedenSıl,isaretle,TumunuTemizle,Guncelleme})(App);//4.adım
